// Counter.jsx
import React from "react";
import "./Counter.scss";

const state = {
  count: 0,
};

const IncrementItem = () => {
  this.setState({ count: this.state.count + 1 });
};

const DecrementItem = () => {
  this.setState({ count: this.state.count - 1 });
};

const Counter = () => {
  return (
    <div className="EmployeeCard__counter">
      <h3>Counter</h3>
      <section className="counter__container">
        <button className="counter__buttons__increment" onClick={this.IncrementItem}>
          <i className="fas fa-arrow-circle-up"></i>
        </button>
        <h5 className="counter__amount">{state}</h5>
        <button className="counter__buttons__decrement" onClick={this.DecrementItem}>
          <i className="fas fa-arrow-circle-down"></i>
        </button>
      </section>
    </div>
  );
};

export default Counter;
